import ReactPlayer from 'react-player'
import gameStartingSoon from '../assets/Game_starting_soon.mp4'
import gameStartingSoon2 from '../assets/Game_starting_soon_2.mp4'
import hadAGreatDay from '../assets/Hope_everyone_had_a_great.mp4'
import { useEffect, useState } from 'react'
import pic from '../assets/2dbomb.png'

function Videoplayer({ gameHasStarted }) {
    const [shouldGreet, setShouldGreet] = useState(false)
    const oneOrTwo = Math.floor(Math.random() * 2) + 1;
    useEffect(() => {
        if (gameHasStarted) {
            setTimeout(() => { // After game has started greet after 2 seconds
                setShouldGreet(true);
            }, 2000);
        } else {
            setTimeout(() => { // After game has started greet after 2 seconds
                setShouldGreet(true);
            }, 1000);
        }
    }, [gameHasStarted])
    return (
        <div>
            {!gameHasStarted && (
                oneOrTwo === 1 ? (
                    <ReactPlayer url={gameStartingSoon} playing={true} onEnded={() => { shouldGreet(false)}} width={200} height={200} />
                ) : (
                    <ReactPlayer url={gameStartingSoon2} playing={true} onEnded={() => { shouldGreet(false)}} width={200} height={200} />
                )
            )}
            {gameHasStarted && (
                oneOrTwo === 1 ? (
                    <ReactPlayer url={hadAGreatDay} playing={shouldGreet} onEnded={() => { shouldGreet(false)}} width={200} height={200} />
                ) : (
                    <ReactPlayer url={hadAGreatDay} playing={shouldGreet} onEnded={() => { shouldGreet(false)}} width={200} height={200} />
                )
            )}
            {gameHasStarted && !shouldGreet && (
                <img src={pic} alt="Picture of AI" width={200} height={200} />
            )}
        </div>
    );
}

export default Videoplayer;
