import React, { useState } from 'react';

function TikTok() {
    const { WebcastPushConnection } = require('tiktok-live-connector');
    const [inputValue, setInputValue] = useState('');

    function connect(tiktokUsername) {
        // Create a new wrapper object and pass the username
        let tiktokLiveConnection = new WebcastPushConnection(tiktokUsername);
        // Connect to the chat (await can be used as well)
        tiktokLiveConnection.connect().then(state => {
            console.info(`Connected to roomId ${state.roomId}`);
        }).catch(err => {
            console.error('Failed to connect with username ' + inputValue, err);
        })

        // Define the events that you want to handle
        // In this case we listen to chat messages (comments)
        tiktokLiveConnection.on('chat', data => {
            console.log(`${data.uniqueId} (userId:${data.userId}) writes: ${data.comment}`);
        })

        // Receive gifts sent to the streamer
        tiktokLiveConnection.on('gift', data => {
            console.log(`${data.uniqueId} (userId:${data.userId}) sends ${data.giftId}`);
        })
    }

    // Function to handle button click
    const handleButtonClick = () => {
        // Call your function with inputValue as a parameter
        //connect(inputValue);
    };

    // Function to handle input change
    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };
    return (
        <div>
            <input
                type="text"
                value={inputValue}
                onChange={handleInputChange}
                placeholder="Enter Tiktok username"
            />
            <button onClick={handleButtonClick}>Connect to live</button>
        </div>
    );
}

export default TikTok;
