import { useEffect } from 'react';
import './App.css';
import Matter from 'matter-js';

function App() {
  const engine = Matter.Engine.create()
  engine.gravity.y = 0
  const world = engine.world

  const canvasWidth = 800
  const canvasHeight = 200

  const render = Matter.Render.create({
    element: document.body,
    engine: engine,
    options: {
      width: canvasWidth,
      height: canvasHeight,
      showAngleIndicator: true,
      showCollisions: true,
      hasBounds: true,
      wireframes: false,
      background: '#fff'
    }
  });


  Matter.Render.run(render);

  // create runner
  const runner = Matter.Runner.create();
  Matter.Runner.run(runner, engine);


  // add bodies
  Matter.Composite.add(world, [
    // walls - Created with (xpos, ypos, width, height)
    Matter.Bodies.rectangle(canvasWidth / 2, 0, canvasWidth, 20, { isStatic: true }), // Top
    Matter.Bodies.rectangle(canvasWidth / 2, canvasHeight, 800, 20, { isStatic: true }), // Bottom
    Matter.Bodies.rectangle(canvasWidth, canvasHeight / 2, 20, 800, { isStatic: true }), // Bottom
    Matter.Bodies.rectangle(0, render.canvas.height / 2, 20, render.canvas.height, { isStatic: true }), // Bottom
  ]);

  const startPlayer = Matter.Bodies.rectangle(render.canvas.width / 2, render.canvas.height / 2, 20, 20,
    {
      frictionAir: 0.03,
      density: 0.3,
      friction: 0.8,
    }
  )
  Matter.Composite.add(world, [startPlayer])


  function spawnPlayer() {
    //Math.random()
    const player = Matter.Bodies.rectangle(render.canvas.width / 2, render.canvas.height / 2, 20, 20,
      {
        frictionAir: 0.03,
        density: 0.3,
        friction: 0.8,
      }
    )
    return player
  }



  function addPlayer() {
    console.log("Player added")
    const player = spawnPlayer()
    Matter.Composite.add(world, [player])
  }

  function move() {
    Matter.Body.applyForce(startPlayer, {
      x: startPlayer.position.x,
      y: startPlayer.position.y
    }, { x: 0.5, y: 0 })
