    const square = Matter.Bodies.rectangle(400, 300, 50, 50, { frictionAir: 0.05 });
    Matter.World.add(engine.current.world, [square]);

    // Run the engine
    Matter.Engine.run(engine.current);
    Matter.Render.run(render);


