    Matter.Render.run(render);

    // create runner
    const runner = Matter.Runner.create();
    Matter.Runner.run(runner, engine);


    // add bodies
    Matter.Composite.add(world, [
      // walls
      Matter.Bodies.rectangle(render.canvas.width / 2, 0, 800, 10, { isStatic: true }),
