import './App.css';
import Matter from 'matter-js';
import bombImage from './assets/2dbomb.png'
//import testProfilePic from './assets/profilepicture.jpg'
import explosion from './assets/explosion.wav';
import ding from './assets/ding.mp3'
import { useState } from 'react';
import ConfettiExplosion from 'react-confetti-explosion';


function App() {
  const [confetti, setConfetti] = useState(false)
  const engine = Matter.Engine.create()
  engine.gravity.y = 0
  const world = engine.world

  const canvasWidth = 800
  const canvasHeight = 200

  const render = Matter.Render.create({
    element: document.body,
    engine: engine,
    options: {
      width: canvasWidth,
      height: canvasHeight,
      showAngleIndicator: true,
      showCollisions: true,
      hasBounds: true,
      wireframes: false,
      background: '#fff'
    }
  });

  Matter.Render.run(render);

  // create runner
  const runner = Matter.Runner.create();
  Matter.Runner.run(runner, engine);

  const wallThichness = 1
  Matter.Composite.add(world, [
    // walls - Created with (xpos, ypos, width, height)
    Matter.Bodies.rectangle(canvasWidth / 2, 0, canvasWidth, wallThichness, { isStatic: true }), // Top
    Matter.Bodies.rectangle(canvasWidth / 2, canvasHeight, canvasWidth, wallThichness, { isStatic: true }), // Bottom
    Matter.Bodies.rectangle(0, canvasHeight / 2, wallThichness, canvasHeight, { isStatic: true }), // Left
    Matter.Bodies.rectangle(0, canvasHeight / 2, 50, canvasHeight, { isStatic: true, render: { fillStyle: "grey" }, collisionFilter: { category: 0x0002 } }), // Start object. Up to 32 different collision categories. 0x0001 is default
    Matter.Bodies.rectangle(canvasWidth, canvasHeight / 2, 50, canvasWidth, { isStatic: true, render: { fillStyle: "green" }, collisionFilter: { category: 0x0002 } }), // Right - Finish line. 
  ]);

  const startPlayer = createPlayer("Startplayer")
  Matter.Composite.add(world, [startPlayer])


  function createPlayer(label, profilePic) {
    Matter.Bodies.rectangle(canvasWidth / 2, canvasHeight / 2, 10, 10, { isStatic: true, render: { sprite: { texture: bombImage, xScale: 0.1, yScale: 0.1 } } }) // bomb
    const player = Matter.Bodies.rectangle(12, render.canvas.height / 2, 20, 20,
      {
        frictionAir: 0.03,
        density: 0.3,
        friction: 0.8,
        label: label,
        collisionFilter: { mask: 0x0001, }, // Should be able to hit each other
        //render: {sprite: {texture: testProfilePic, xScale: 0.04, yScale: 0.04}} // uncomment this for profile pic
      }
    )
    return player
  }

  async function spawnBomb() {
    const bomb = Matter.Bodies.rectangle(canvasWidth / 2, canvasHeight / 2, 10, 10, { isStatic: true, render: { sprite: { texture: bombImage, xScale: 0.1, yScale: 0.1 } } })
    Matter.Composite.add(world, [bomb])
    playSound(ding)
    await new Promise(resolve => setTimeout(resolve, 1500));
    playSound(explosion, 0.25)
    Matter.Composite.remove(world, [bomb])
    explode(bomb.position)
  }

  // Creates consequense of blast radius
  function explode(bombPosition) {
    const allBodies = Matter.Composite.allBodies(world)
    console.log(allBodies)
    allBodies.forEach(player => {
      const distanceToBomb = bombPosition.x - player.position.x
      if (player.label === 'Rectangle Body') {
        return
      }
      // later: check if they are safe from blast. If they have bought safety. Could also add effect on y-axis
      if (distanceToBomb < 20) { // player dies
        Matter.Composite.remove(world, [player])
      } else if (distanceToBomb > 20 && distanceToBomb < 80) { // gets a blast
        Matter.Body.applyForce(player, player.position, { x: -2, y: 0 })
      } else if (distanceToBomb > 100 && distanceToBomb < 120) {
        Matter.Body.applyForce(player, player.position, { x: -1, y: 0 })
      }
    });
  }

  function playSound(file, volume = 1) {
    const audio = new Audio(file)
    audio.volume = volume
    audio.play()
  }

  function spawnPlayer() {
    console.log("Player added")
    const player = createPlayer(Math.random())
    Matter.Composite.add(world, [player])
  }

  function move(username) {
    //Matter.Body.applyForce(startPlayer, {
    //  x: startPlayer.position.x,
    //  y: startPlayer.position.y
    //}, { x: 0.5, y: 0 })
    const allBodies = Matter.Composite.allBodies(world)
    const randomIndex = Math.floor(Math.random() * (allBodies.length - 4)) + 4;
    const randomPlayer = allBodies[randomIndex]
    /*
    const foundPlayer = allBodies.find(player => player.label === username);
    if (foundPlayer) {
      Matter.Body.applyForce(foundPlayer, foundPlayer.position, { x: 0.5, y: 0 })
    } else {
      console.log("Object not found with name:", username);
    }
    */
    Matter.Body.applyForce(randomPlayer, randomPlayer.position, { x: 5, y: 0 })
    // check if in goal
    if (randomPlayer.position.x === canvasWidth-50) {
      console.log("Game has ended. Winner: " + random)
    }
  }

  return (
    <div className="App">
      <button onClick={spawnPlayer}>Ny spiller</button>
      <button onClick={() => { move("Startplayer") }}>Boost</button>
      <button onClick={spawnBomb}>Bomb</button>
      {/*<button onClick={() => { setConfetti(true) }}><>{confetti && <ConfettiExplosion />}</>Confetti</button>*/}
    </div>
  );
}

export default App;
