import { useEffect } from 'react';
import './App.css';
import Matter from 'matter-js';

function App() {
  const engine = Matter.Engine.create()
  engine.gravity.y = 0
  const world = engine.world

  const canvasWidth = 800
  const canvasHeight = 200

  const render = Matter.Render.create({
    element: document.body,
    engine: engine,
    options: {
      width: canvasWidth,
      height: canvasHeight,
      showAngleIndicator: true,
      showCollisions: true,
      hasBounds: true,
      wireframes: false,
      background: '#fff'
    }
  });


  Matter.Render.run(render);

  // create runner
  const runner = Matter.Runner.create();
  Matter.Runner.run(runner, engine);


  // add bodies
  const wallThichness = 1
  Matter.Composite.add(world, [
    // walls - Created with (xpos, ypos, width, height)
    Matter.Bodies.rectangle(canvasWidth / 2, 0, canvasWidth, wallThichness, { isStatic: true }), // Top
    Matter.Bodies.rectangle(canvasWidth / 2, canvasHeight, canvasWidth, wallThichness, { isStatic: true }), // Bottom
    Matter.Bodies.rectangle(0, canvasHeight / 2, wallThichness, canvasHeight, { isStatic: true }), // Left
    Matter.Bodies.rectangle(canvasWidth, canvasHeight / 2, wallThichness, canvasWidth, { isStatic: true }), // Right
  ]);

  const startPlayer = spawnPlayer()
  Matter.Composite.add(world, [startPlayer])


  function spawnPlayer() {
    //Math.random()
    const player = Matter.Bodies.rectangle(20, render.canvas.height / 2, 20, 20,
      {
        frictionAir: 0.03,
        density: 0.3,
        friction: 0.8,
        label
      }
    )
    return player
  }



  function addPlayer() {
    console.log("Player added")
    const player = spawnPlayer()
    Matter.Composite.add(world, [player])
  }

  function move() {
    Matter.Body.applyForce(startPlayer, {
      x: startPlayer.position.x,
      y: startPlayer.position.y
    }, { x: 0.5, y: 0 })
    //console.log(Matter.Composite.allBodies(world))
  }
  useEffect(() => {
  }, [])

  return (
    <div className="App">
      <button onClick={addPlayer}>Ny spiller</button>
      <button onClick={move}>Boost</button>
    </div>
  );
}

export default App;
